import React, {Component} from 'react';
import '../Header/Header.css'


class SearchBar extends Component {

    state = {
        value:''
    }

    timeout =null;
    doSearch = (event) =>{
        this.setState({value:event.target.value});
        clearTimeout(this.timeout);

        this.timeout = setTimeout( () => {
            this.props.callback(this.state.value); // wont work unless if you are not sending the call back function from the home page
        }, 500);
    }
    render (){

        return (
            <>
            {/* <div class="search-icon">
                <span class="fas fa-search"></span>
            </div>
            <div class="cancel-icon">
                <span class="fas fa-times"></span> */}
            {/* </div> */}

            <input type="search" class="search-data" placeholder="Search" required required onChange={this.doSearch} value={this.state.value}/>
            <button type="submit" class="fas fa-search"></button>

        </>
        )
    }
}
export default SearchBar;
