import React from 'react';
import moment from 'moment';

const HomeMain = (props) =>{
    return (
        // <div className="test">
        //     {props.clickable ?
        //     <h1>{props.author}</h1>
        //     :null}
        // </div>
            <div className="HomeMain-wrapper">
                <h1>{props.author? props.author : <h1>na</h1>}</h1>
                 <div className="blog-post">
                     <div className="blog-post_img">
                         <img src={props.image} alt=""></img>
                     </div>
                 </div>
                 <div className="blog-post_info">
                     <div className="blog-post_date">
                         <span> {moment(props.date).format("dddd")} </span>
                         <span> {moment(props.date).format("MMMM do YYYY")} </span>
                     </div>
                     <h1 className="blog-post_title">{props.title}</h1>
                     <p className="blog-post_text">{props.content}</p>
                     <a rel="noopener noreferrer" href="http://url.com" target="_blank" className="blog-post_cta">Read More</a>
                     <a rel="noopener noreferrer" href="http://url.com" target="_blank">Link Here</a>

                 </div>
             </div>




    )
}

export default HomeMain;
